MD_PARTICIPANT = 1
MESSAGE_DIRECTOR = 2
CLIENT = 3
STATESERVER = 4
DB_STATESERVER = 5
OBJECT = 6
DATABASE = 7
AI = 8
UBER = 9
RESERVED_MAX = 11

ZONE_BITS = 32

PARENT_PREFIX = 1 << ZONE_BITS
DATABASE_PREFIX = 2 << ZONE_BITS


def locationAsChannel(parent, zone):
    return (parent << ZONE_BITS) | zone
